import React, { useEffect, useState } from "react";
import { GoogleMap, useLoadScript, Marker, Polygon } from "@react-google-maps/api";
import { REACT_APP_GOOGLE_MAPS_KEY } from "../constants/constants";

const MapComponent = ({ selectedLocation }) => {
  const { isLoaded, loadError } = useLoadScript({
    googleMapsApiKey: REACT_APP_GOOGLE_MAPS_KEY,
  });
  const [hyderabadPincodes, setHyderabadPincodes] = useState([]);

  // Dummy data for pincodes in Hyderabad
  const hyderabadPincodesData = [
    { pincode: "500001", coordinates: [ { lat: 17.39, lng: 78.47 },{ lat: 17.33, lng: 78.47 }]},
    { pincode: "500002", coordinates: [ { lat: 17.51, lng: 78.46 },{ lat: 17.33, lng: 78.47 }]},
    { pincode: "500003", coordinates: [ { lat: 17.50, lng: 78.50 },{ lat: 17.33, lng: 78.47 }]},
  ];

  useEffect(() => {
    // Simulating fetching pincodes data from API
    setHyderabadPincodes(hyderabadPincodesData);
  }, []);

  const mapContainerStyle = {
    height: "500px",
    width: "100%"
  };

  const center = {
    lat: 17.3850,
    lng: 78.4867
  };

  const zoom = 12;

  const onMapLoad = React.useCallback((map) => {
    // Initialize map
  }, []);

  if (loadError) return "Error loading maps";
  if (!isLoaded) return "Loading maps";

  return (
    <div style={{ marginTop: "50px" }}>
      <GoogleMap
        mapContainerStyle={mapContainerStyle}
        center={center}
        zoom={zoom}
        onLoad={onMapLoad}
      >
        {/* Render Markers for Pincodes */}
        {hyderabadPincodes.map(pincodeData => (
          <Marker key={pincodeData.pincode} position={{ lat: pincodeData.coordinates[0].lat, lng: pincodeData.coordinates[0].lng }} />
        ))}

        {/* Render Polygons for Pincodes */}
        {hyderabadPincodes.map(pincodeData => (
          <Polygon
            key={pincodeData.pincode}
            paths={pincodeData.coordinates}
            options={{
              strokeColor: "#FF0000",
              strokeOpacity: 0.8,
              strokeWeight: 2,
              fillColor: "#FF0000",
              fillOpacity: 0.35,
            }}
          />
        ))}
      </GoogleMap>
    </div>
  );
};

export default MapComponent;
