// MapComponent.js
import React from "react";
import { GoogleMap, useLoadScript, Polygon,Mar } from "@react-google-maps/api";
import { REACT_APP_GOOGLE_MAPS_KEY } from "../constants/constants";

const MapComponent = () => {
  const { isLoaded, loadError } = useLoadScript({
    googleMapsApiKey: REACT_APP_GOOGLE_MAPS_KEY,
    libraries: ["places"],
  });

  const mapContainerStyle = {
    width: "100%",
    height: "100%",
  };

  const center = {
    lat: 17.385044, // Hyderabad's approximate center coordinates
    lng: 78.486671,
  };

  const zoom = 10; // Adjust zoom level as needed

  const pincodeAreas = [
    {
      pincode: "500001",
      name: "Area A, Hyderabad",
      coordinates: [
        { lat: 17.390617, lng: 78.477359 },
        { lat: 17.391124, lng: 78.492725 },
        { lat: 17.383285, lng: 78.492063 },
        { lat: 17.383058, lng: 78.477131 },
      ],
    },
    {
      pincode: "500002",
      name: "Area B, Hyderabad",
      coordinates: [
        { lat: 17.405326, lng: 78.464421 },
        { lat: 17.403832, lng: 78.472459 },
        { lat: 17.396382, lng: 78.471431 },
        { lat: 17.397671, lng: 78.463634 },
      ],
    },
    {
      pincode: "500003",
      name: "Area C, Hyderabad",
      coordinates: [
        { lat: 17.407815, lng: 78.495585 },
        { lat: 17.406174, lng: 78.5071 },
        { lat: 17.398297, lng: 78.504915 },
        { lat: 17.400151, lng: 78.494723 },
      ],
    },
  ];

  if (loadError) return "Error loading maps";
  if (!isLoaded) return "Loading maps";

  return (
    <div style={{ height: "100vh", width: "100%" }}>
      <GoogleMap
        mapContainerStyle={mapContainerStyle}
        center={center}
        zoom={zoom}
      >
        {/* Render polygons for each pincode area */}
        {pincodeAreas.map((area, index) => (
          <React.Fragment key={index}>
            {/* Fill the polygon with a color */}
            <Polygon
              paths={area.coordinates}
              options={{
                fillColor: "green", // Fill color for the area
                fillOpacity: 0.35,
                strokeColor: "red", // Border color for the area
                strokeOpacity: 0.8,
                strokeWeight: 2, // Border thickness
              }}
            />
            {/* Add text label for the pincode area */}
            <Marker position={getPolygonCenter(area.coordinates)} label={area.pincode} />
          </React.Fragment>
        ))}
      </GoogleMap>
    </div>
  );
};

// Helper function to calculate the center of the polygon
function getPolygonCenter(path) {
  const bounds = new window.google.maps.LatLngBounds();
  path.forEach((point) => {
    bounds.extend(point);
  });
  return {
    lat: bounds.getCenter().lat(),
    lng: bounds.getCenter().lng(),
  };
}

export default MapComponent;
