// MapComponent.js
import React from "react";
import { GoogleMap, useLoadScript, Polygon } from "@react-google-maps/api";
import { REACT_APP_GOOGLE_MAPS_KEY } from "../constants/constants";

const MapComponent = () => {
  const { isLoaded, loadError } = useLoadScript({
    googleMapsApiKey: REACT_APP_GOOGLE_MAPS_KEY,
    libraries: ["places"],
  });

  const mapContainerStyle = {
    width: "100%",
    height: "100%",
  };

  const center = {
    lat: 12.9716, // Example center coordinates (adjust as needed)
    lng: 77.5946,
  };

  const zoom = 10; // Example zoom level (adjust as needed)

  const boundaries = [
    {
      name: "Area A",
      coordinates: [
        { lat: 12.9716, lng: 77.5946 },
        { lat: 12.9616, lng: 77.6046 },
        { lat: 12.9516, lng: 77.5946 },
        { lat: 12.9616, lng: 77.5846 },
      ],
    },
    {
      name: "Area B",
      coordinates: [
        { lat: 12.9816, lng: 77.6146 },
        { lat: 12.9716, lng: 77.6246 },
        { lat: 12.9616, lng: 77.6146 },
        { lat: 12.9716, lng: 77.6046 },
      ],
    },
  ];

  if (loadError) return "Error loading maps";
  if (!isLoaded) return "Loading maps";

  return (
    <div style={{ height: "100vh", width: "100%" }}>
      <GoogleMap
        mapContainerStyle={mapContainerStyle}
        center={center}
        zoom={zoom}
      >
        {/* Render polygons for each boundary */}
        {boundaries.map((area, index) => (
          <Polygon
            key={index}
            paths={area.coordinates}
            options={{
              strokeColor: "#FF0000", // Red stroke color
              strokeOpacity: 0.8,
              strokeWeight: 2,
              fillColor: "#FF0000", // Red fill color (adjust as needed)
              fillOpacity: 0.35,
            }}
          />
        ))}
      </GoogleMap>
    </div>
  );
};

export default MapComponent;
