// Map.js
import React, { useState } from "react";
import { GoogleMap, useLoadScript, Polygon } from "@react-google-maps/api";
import { REACT_APP_GOOGLE_MAPS_KEY } from "../constants/constants";

const MapComponent = ({ selectedLocation }) => {
  const { isLoaded, loadError } = useLoadScript({
    googleMapsApiKey: REACT_APP_GOOGLE_MAPS_KEY,
    libraries: ["places"],
  });
  const [hyderabadPincodesData, setHyderabadPincodesData] 
    = useState([]);

  // Example data for pincode boundaries in Hyderabad
  

  const mapContainerStyle = {
    width: "100%",
    height: "100%",
  };

  const center = {
    lat: selectedLocation.lat,
    lng: selectedLocation.lng,
  };

  const zoom = 12;

  if (loadError) return "Error loading maps";
  if (!isLoaded) return "Loading maps";

  return (
    <div style={{ height: "100%", width: "100%" }}>
      <GoogleMap
        mapContainerStyle={mapContainerStyle}
        center={center}
        zoom={zoom}
      >
        {hyderabadPincodesData.map((pincodeData, index) => (
          <Polygon
            key={index}
            paths={pincodeData.coordinates}
            options={{
              strokeColor: "#FF0000", // Red stroke color
              strokeOpacity: 0.8,
              strokeWeight: 2,
              fillColor: "#FF0000", // Red fill color (adjust as needed)
              fillOpacity: 0.35,
            }}
          />
        ))}
      </GoogleMap>
    </div>
  );
};

export default MapComponent;
